		// 					shadowColor = min( shadowColor, vec3( shadowDarkness[ i ] ) );

			#endif

		}


		#ifdef SHADOWMAP_DEBUG

			if ( inFrustum ) outgoingLight *= frustumColors[ i ];

		#endif

